import pandas as pdimport numpy as npdata = np.random.randint(10,100,15).reshape(5,3)df= pd.DataFrame(data, index = ["a","c","e","f","h"], columns = ["column1","column2","column3"])df = df.reindex(["a","b","c","d","e","f","g","h"])newColumn = [np.nan,30,np.nan,44,np.nan,34,np.nan,10]df["column4"] = newColumnresult = dfresult= df.drop("column1",axis=1) # axis 1 sütun 0 satırresult= df.drop(["column1","column2"],axis = 1)result= df.drop("a", axis = 0)result= df.drop(["a","b","h"], axis = 0)result = df.isnull()#null olanlar true dönerresult = df.notnull()#null olmayanlar true döner result = df.isnull().sum()result = df["column1"].isnull().sum()result = df[df["column1"].isnull()]#tüm kolonların boş değerlerini gösteririresult = df[df["column1"].isnull()]["column1"]# sadece kolon 1 de olan boş değerleri gösterresult = df[df["column1"].notnull()]["column1"]# sadece kolon 1 deki not null değerleri gösterresult = df[df["column1"].notnull()] # tüm kolonların notnull değerlerini gösterirresult = df.dropna() #drop not a number axis = 0 result = df.dropna(axis = 1) #sütun içinresult = df.dropna(how = "any") result = df.dropna(how = "all")result = df.dropna(subset = ["column1","column2"], how = "all") #col1 ve col2 de ara diğerlerinde değil. tümü nan'sa silresult = df.dropna(subset = ["column1","column2"], how = "any") #col1 ve col2 de ara diğerlerinde değil. bir tane bile nan varsa silresult = df.dropna(thresh = 2) #en az 2 adet normal veri kabul edilebilir.result = df.dropna(thresh = 3) # 3 normal 1 nan olabilirresult = df.fillna(value = "no input") #nan'ları bu değerle değiştirresult = df.sum()#satıların toplam değerleriresult = df.sum().sum()#tüm satıların toplamıresult = df.sizeresult = df.isnull().sum()#isnulları ayrı ayrı hesaplaresult = df.isnull().sum().sum()#tüm satır isnulları topla#tüm nullara hesaplanan ortalam değeri yazdırıldıdef ortalama(df):    toplam = df.sum().sum() # toplam değerdir    adet = df.size - df.isnull().sum().sum() # size dan tüm toplamı çıkart    return toplam / adet # toplam ve  adetin bölümdüdürresult = df.fillna(value = ortalama(df))print(result)print(df)