import matplotlib.pyplot as plt import numpy as npx = [1,2,3,4,5]y = [10,18,15,25,40]plt.plot(x,y, color="blue", linewidth="1")#plt.axis([0,10,0,50]) # x ve y nin maksimum değerlerini verir listeler.plt.title("Grafik başlığı")plt.xlabel("x label")plt.ylabel("y label")#linear quadratic cubic x = np.linspace(0,2,100)plt.plot(x,x, label = "linear")plt.plot(x,x**2, label = "quadratic")plt.plot(x,x**3, label = "cubic")plt.xlabel("x label")plt.ylabel("y label")plt.legend()#birden fazla grafikfig,axs = plt.subplots(3)axs[0].plot(x,x, color="red")axs[0].set_title("linear")axs[1].plot(x,x**2, color="green")axs[1].set_title("quadratic")axs[2].plot(x,x**3,color="blue")axs[2].set_title("cubic")plt.tight_layout()# 4 lü grafikx = np.linspace(0,2,100)fig,axs = plt.subplots(2,2)fig.suptitle("grafik başlığı")axs[0,0].plot(x,x, color="red")axs[0,1].plot(x,x**2, color="blue")axs[1,0].plot(x,x**3,color="green")axs[1,1].plot(x,x**4,color="yellow")plt.show()