from instagramUserInfo import username, passwordfrom selenium import webdriverfrom selenium.webdriver.common.by import Byfrom selenium.webdriver.common.keys import Keysimport timeclass Instagram:    def __init__(self, username, password):        self.browser = webdriver.Safari()  # or webdriver.Chrome()        self.username = username        self.password = password            def signIn(self):        self.browser.get("https://www.instagram.com/accounts/login/")        time.sleep(3)        username_input = self.browser.find_element(By.XPATH, "//*[@id='loginForm']/div[1]/div[1]/div/label/input")        password_input = self.browser.find_element(By.XPATH, "//*[@id='loginForm']/div[1]/div[2]/div/label/input")        username_input.send_keys(self.username)        password_input.send_keys(self.password)        password_input.send_keys(Keys.ENTER)        time.sleep(5)  # Wait for login to complete            # Take following list    def getFollowers(self):        self.browser.get(f"https://www.instagram.com/{self.username}")                followersLink = self.browser.find_element_by_xpath("//*[@id='mount_0_0_kt']/div/div/div[2]/div/div/div[1]/div[2]/div/div[1]/section/main/div/header/section[3]/ul/li[2]/div/a/span")        followersLink.click()        # Create instance and sign ininsta = Instagram(username, password)insta.signIn()insta.getFollowers()