import pandas as pd#sql de bulunan join konusu işleniyor. customer ordera atanıyorcustomers = {    "CustomerId": [1,2,3,4],    "FirstName" : ["Ahmet", "Ali","Hasan","Canan"],    "LastName" : ["Yılmaz", "Korkamaz","Çelik","Toprak"]    }orders = {    "OrderId": [10,11,12,13],    "CustomerId": [1,2,5,7],    "OrderDate": ["2020-07-04","2022-07-04","2023-07-04","2026-07-04"]        }df_customers = pd.DataFrame(customers,columns=["CustomerId","FirstName","LastName"])df_orders = pd.DataFrame(orders,columns =["OrderId","CustomerId","OrderDate"])print(df_customers)print(df_orders)result = pd.merge(df_customers,df_orders,how="inner")result = pd.merge(df_customers,df_orders,how="left")#siparişi olmayan kullanıcılarprint(result)customersA = {    "CustomerId": [1,2,3,4],    "FirstName" : ["Ahmet", "Ali","Hasan","Canan"],    "LastName" : ["Yılmaz", "Korkamaz","Çelik","Toprak"]    }customersB = {    "CustomerId": [4,5,6,7],    "FirstName" : ["Yağmur", "Osman","Kerem","Can"],    "LastName" : ["yılar", "korkar","demir","su"]    }df_customersA = pd.DataFrame(customersA,columns=["CustomerId","FirstName","LastName"])df_customersB = pd.DataFrame(customersB,columns=["CustomerId","FirstName","LastName"])result = pd.concat([df_customersA,df_customersB]) #birleştirme işlemiprint(result)