import pandas as pddf= pd.read_csv("/Users/enessalihgokcek/Desktop/python/trendingYoutubeStatistics/USvideos.csv")# 1- ilk 10 kaydı getiriniz result = df.head(10)# 2- ikinci 5 kaydı getirinizresult = df[5:10].head(5)# 3- Dataset'de bulunan kolon isimleri ve sayısını bulunuzresult = df.columnsresult = len(df.columns)# 4- Aşağıad bıulunan bazı kolonları silin ve kalan kolonları listeleyin.# (thumbnail_link,comments_disabled,ratings_disabled)df.drop(["thumbnail_link","comments_disabled","ratings_disabled"], axis =1, inplace=True)# 5- beğenme ve beğenmeme sayılarının ortalamasını bulunuzlikeRate = df["likes"].mean()dislikeRate = df["dislikes"].mean()print(likeRate)print(dislikeRate)# 6- ilk 50 videonun like ve dislike kolonlarını getirinizresult = df.head(50)[["title","likes","dislikes"]]# 7- en cok görüntülenen video hangisidirresult = df[df["views"].max() == df["views"]].iloc[0]# 8- en düşük görüntülenme alan video hangisiresult = df[df["views"].min() == df["views"]].iloc[0]# 9-en fazla görüntülenen ilk 10 videoresult = df.sort_values("views", ascending =False).head(10)[["title","views"]]# 10- Kategoriye göre beğeni ortlaamlarını sıralı şekilde getirinizresult = df.groupby("category_id")[["likes"]].mean().sort_values("likes")# 11- Kategoriye göre yorum sayılarını yıkarıdan aşğıya sıralayınızresult = df.groupby("category_id").value_counts()# 12- Her kategoride kaç video vardır?result = df["category_id"].value_counts()# 13- Her videonun title uzunluğu bilgisini yeni bir kolonda gösterinizdf["title_len"] = df["title"].apply(len)# 14- Her video için kullanılan tag sayısını yeni kolonda gösteriniz# df["tag_count"] = df["tags"].apply(lambda x: len(x.split('|')))def tagCount(tag):    return len(tag.split('|'))df["tag_count"] = df["tags"].apply(tagCount)# 15-En popüler videoları listeleyiniz(like dislike oranın göre)likeList = list(df["likes"].head())dislikeList = list(df["dislikes"])print(likeList,dislikeList)def likeDislikeOranHesapla(dataset):    likeList = list(dataset["likes"])    dislikeList = list(dataset["dislikes"])        liste = list(zip(likeList, dislikeList))        oranListesi = []        for like,dislike in liste:        if(like + dislike) == 0:            oranListesi.append(0)        else:            oranListesi.append(like/(like+dislike))                return oranListesi             df["beğeni_orani"] = likeDislikeOranHesapla(df)result = df.sort_values("beğeni_orani",ascending=False)[["title","likes","beğeni_orani"]]  print(result)