import pandas as pddata = pd.read_csv("nba_players.csv")data.dropna(inplace = True)data["Player"] = data["Player"].str.upper()data["Player"] = data["Player"].str.lower()data["index"] = data["Player"].str.find("a")data = data.Team.str.replace("","-")#tüm boşları cizgi ile değişdata[["FirstName","LastName"]] = data["Player"].loc[data["Player"].str.split().str.len()==2].str.split(expand=True)print(data)