import mysql.connectorconnection = mysql.connector.connect(    host="localhost",    user="root",    password="Enessalih29.34",    database="schoolDb")class Student :     connection = connection    mycursor = connection.cursor()        def __init__(self, studentNumber,name,surname,birthdate,gender):        self.studentNumber = studentNumber        self.name = name        self.surname = surname        self.birthdate = birthdate  # datetime nesnesi olarak bekleniyor        self.gender = gender            def saveStudent(self):        sql = "INSERT INTO Student(studentNumber, name, surname, birthdate, gender) VALUES (%s, %s, %s, %s, %s)"        values = (self.studentNumber, self.name, self.surname, self.birthdate, self.gender)        try:            Student.mycursor.execute(sql, values)            Student.connection.commit()            print(f"{Student.mycursor.rowcount} kayıt eklendi.")        except mysql.connector.Error as err:            print("Hata: ", err)        finally:            Student.connection.close()            print("database bağlantısı kapandı")            @staticmethod    def saveStudents(students):        sql = "INSERT INTO Student(studentNumber,name,surname,birthdate,gender)"        values = students        Student.mycursor.executemany(sql,values)                try:            Student.connection.commit()            print(f"{Student.mycursor.rowcount} tane kayıt eklendi.")        except mysql.connector.Error as err:            print("hata : ", err)        finally:            Student.connection.close()    @staticmethod    def StudentInfo():        #sql = "SELECT * FROM student"        #sql = "SELECT name,surname from student where gender='K'"        #sql = "SELECT * from student where YEAR(birthdate) = 2005 and name = 'Ali'"        #sql = "SELECT * from student where name like 'an%' or surname like'an%'"        #sql = "SELECT COUNT(*) from student where gender='E' "        sql = "SELECT name,surname from student where gender='K' order by name,surname"        Student.mycursor.execute(sql)                try:            results = Student.mycursor.fetchall()                        for result in results:                print(result)        except mysql.connector.Error as err:            print("hata", err)        finally:            Student.connection.close()                def udpateProduct(name,surname):        connection = mysql.connector.connect(            host = "localhost",            user = "root",            password = "Enessalih29.34",            database = "schoolDb"                        )        cursor = connection.cursor()                sql = "UPDATE Student Set name = %s surname = %s where id=%s"        values = (name,surname)        cursor.execute(sql,values)                try:            Student.cursor.execute(sql, values)            Student.connection.commit()            print(f"{Student.mycursor.rowcount} kayıt eklendi.")        except mysql.connector.Error as err:            print("Hata: ", err)        finally:            Student.connection.close()            print("database bağlantısı kapandı")                            def deleteProduct(id):        connection = mysql.connector.connect(            host = "localhost",            user = "root",            password = "Enessalih29.34",            database = "schoolDb"                        )        cursor = connection.cursor()                sql = "DELETE from student where id = %s"        values = (id,)        cursor.execute(sql,values)                try:            connection.commit()            print(f"{cursor.rowcount} kayıt silindi.")        except mysql.connector.Error as err:            print("Hata: ", err)        finally:            connection.close()            print("database bağlantısı kapandı")    deleteProduct()                            