import mysql.connectordef insertProduct(name,price,imageUrl,description):    connection = mysql.connector.connect(        host="localhost", user="root", password="Enessalih29.34", database="node-app")    cursor = connection.cursor()    sql = "INSERT INTO Product(name,price,imageUrl,description) VALUES (%s,%s,%s,%s)"    values = (name,price,imageUrl,description)    cursor.executemany(sql, values)    try:        connection.commit()        print(f"{cursor.rowcount} tane kayıt eklendi")        print(f"son eklenen kaydın id: {cursor.lastrowid}")    except mysql.connector.Error as err:        print("hata:", err)    finally:        connection.close()        print("database bağlantısı kapandı.")list = []while True:        name = input("ürün adını girin")    price = input("ürün fiyat girin")    imageUrl = input("ürün imageurl girin")    description = input("ürün acıklama girin")        list.append((name,price,imageUrl,description))        result = input("devam etmek istiyor musunuz? (e/h)")    if result == "h":        print("kayıtlarınız aktarılıyor")        print(list)        insertProduct(list)        break        insertProduct(name,price,imageUrl,description)